
public class LocalVariable {
	static int b=1234;
	static String abc="this is an example for global string";
	static String wer="Gimme some sugar I am your neighbor";
	//GlobalVariable example on line above//
	//Static means that you can call a method, even if you've never created the object to which it belongs!//

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Local variable example below
		int a;
		a=123;
		
        String asd;
        asd="this is string for local";
        
        String tyu;
        tyu="Sorry Miss Jackson I am forreal never meant to make your daughter cry, i apologize a trillion times.";
        
        boolean chf=true;
        
       int i=10;
       int o=123;
       int p= 32;
       
       int z= i*o;
       int x= p/i;
       int c= o-p;
       
       
        
   		System.out.println(a);
		System.out.println(b);
		System.out.println(abc);
		System.out.println(asd);
		System.out.println(wer);
		System.out.println(tyu);
		System.out.println(chf);
		System.out.println(z);
		System.out.println(x);
		System.out.println(c);
		
		
	}

}
